	/*********************
	* CSS Ravenous
	* Edição: Del Vieira
	**********************/
	]

	/* Geral */
	@import url('https://fonts.googleapis.com/css2?family=Abel&display=swap');
	body {
	    font-family: 'Open Sans';
		text-transform: uppercase;
	}
	.header__nav a,.product-card .product-card__name,.footer__title,.products-carousel__title a, .products-carousel__title span, .blog .card .card-body h5.card-title, h1.product__name, #product-description p.lead,.products-carousel__title, #page-home .page-title__head h1{
		font-family: 'Abel', sans-serif !important;
		text-transform: uppercase;
	}
	#header,.header,.top-bar,footer.footer, .footer {
	    background: #000 !important;
	    background-color: #000 !important;
	    color: #fff !important;
	}
	#header a,.top-bar a,.footer  a {
	    color: #fff !important;
	}
	.menu__social .fab,
	.footer__social .fab{
	    color: #FFF !important;
	}
	.ecom-credits { 
		padding: 6px;
	}
	.banners-grid .banner {
	    text-align: center;
	    margin: 0 auto !important;
	    display: inline;
	}
	.login-modal__body.card-body button:last-child {
	    background: #333;
	    color: #FFF;
	}
	aside.search-engine__aside, aside.search-engine__aside * {
	    color: #FFF;
	}
	.glide__arrows, .glide__bullets {display: none;}
	.recommended-products .glide__arrows, .recommended-products .glide__bullets {display: block;}
	.search-engine__results.fade .product-card__buy.fade {
	    opacity: 1 !important;
	    display: inline-block;
	    position: relative;
	    margin: 0 auto;
	    width: 100%;
	    padding: 0;
	    transform: none !important;
	    left: auto;
	    right: auto;
	    bottom: auto;
	}
	.search-engine__count strong {
    background: #03a503;
    color: #FFF;
    padding: 5px;
    border-radius: 3px;
    margin-right: 4px;
}
.search-engine__count  {
    color: #FFF;
}
	#page-home .page-title__head {
    position: relative;
        text-align: center;
	}


	#page-home .page-title__head:before {
	position: absolute;
    content: " ";
    height: var(--border-width);
    top: 50%;
    margin-top: -.05rem;
    background: var(--border-color);
	}

	#page-home .page-title__head:before, 
	.products-carousel__title:before {
    width: 98%;
    left: 1%;
	}
	#page-home .page-title__head h1 {
	    font-size: 2rem !important;
	    text-align: center;
	    display: inline-block;
	    margin: 0 auto;
	    position: relative;
	    z-index: 1;
	    background: #000;
    padding: 0 20px;
	}

	/* Header*/
	.header__nav {
	    margin-top:15px;
	}
	.header__nav a {
	    color: #FFF;
	    font-size: 23px;
	    font-weight: lighter !important;
	    font-family: 'Open Sans';
	    letter-spacing: .5px;
	    padding: 0 15px;
	}
	.header__buttons .btn .badge {
	    background: #03a503;
	}

	/* Menu lateral */
	#menu .menu__item {
	    color: #FFF;
	}


	/* Footer */
	.footer .footer__stamps li {
	    margin: 2px 0px;
	    padding: 2px;
	}
	.footer .footer__stamps img {
	    width: auto !important;
	    max-height: 40px !important;
	    background: #FFF;
	}
	.footer p.footer__payment-methods .pay-icon {
	    background-color: #fff !important;
	}
	footer.footer {
	    font-size: 13px;
	    font-weight: lighter;
	    text-transform: uppercase;
	}
	.footer__title {
	    color: #FFF;
	    font-size: 22px;
	}
	.footer ul.footer__links i.fas:before {
	    content: '\00BB';
	    display: inline-block;
	    margin-right: 6px;
	    position: relative;
	    top: -1px;
	}
	#footer-pages .footer__links li:nth-child(5), #footer-pages .footer__links li:nth-child(6), #footer-pages .footer__links li:nth-child(7) {
	    display: none;
	}
	@media screen and (max-width: 768px) {
		.foot-widget {
	    	margin: 17px 0;
	    	text-align: center;
		}
	}

	/* Blog */
	.blog .card .card-body {
	    text-align: center;
	    background: #FFF;
	}
	.blog .card .card-body h5.card-title {
	    font-size: 17px;
	    margin: 0;
	}
	.blog .card {
	    margin-bottom: 15px;
	}

	/* Produtos */
	.product-card {
	    background: #000000;
	    color: #FFF;
	    text-align: center;
	}


	.product-card__offer-stamp,
	.product-card__new-stamp {
	    text-indent: -9999px;
	    width: 64px;
	    height: 22px;
	    background-position: center;
	    background-repeat: no-repeat;
	    right: 7px;
	    opacity: 1;
	}

	.product-card__offer-stamp {
	    background-image: url(http://cdn.e-com.club/s/www.ravenous.com.br/uploads/oferta.png);
	}
	.product-card__new-stamp {
	    background-image: url(http://cdn.e-com.club/s/www.ravenous.com.br/uploads/novo.png);
	}
	#product h1.product__name {
	    font-size: 40px;
	}
	#product .prices strong.prices__value {
	    font-weight: lighter;
	    font-size: 35px;
	    color: #fff;
	    display: inline-block;
	}
	.product-card .product-card__name {
	    margin: 8px 0 !important;
	    padding: 0 0 0 0 !important;
	    height: auto;
	    font-size: 22px;
	    color: #FFF;
	}
	.product-card strong.prices__value {
	    margin: 0 0 0 0 !important;
	    font-size: 20px;
	    font-weight: lighter;
	}
	.product-card__buy button.btn {
	    color: #FFF;
	    background-color: #03a503 !important;
	    opacity: 1 !important;
	    display: block;
	    width: auto;
	    margin: 13px auto 0 auto;
	}
	/* Pagina de Produto*/
	#product-description {
	    text-align: center;
	    color: #FFF;
	}
	#product-specs, section#product-specs table td {
	    color: #FFF !important;
	    text-transform: uppercase;
	}
	#product-description p.lead {
	    font-size: 20px;
	    border-bottom: 1px solid #111;
	    padding-bottom: 10px;
	    color: #FFF;
	    text-transform: uppercase;
	}
	#product-actions .product__buy button.btn {
	    background: #03a503;
	}
	p.product__short-stock,
	p.product__sku,
	.top-bar__nav.d-none.d-md-block {
	    display: none !important;
	}

	#product-actions button.btn.share i.fab, #product-actions button.btn.share i.fas, #product-actions button.btn.share, #product-actions button.btn.share strong {
	    color: #FFF !important;
	}
	#product-actions a#product-payment-header {
	    color: #FFF;
	}
	.shipping-calculator__free-from-value {
	    display: none;
	}

	/* Carrinho de Compra */
	.cart-item__container, .cart-item__container a {
	    color: #FFF;
	}
	.cart-item__container a {
	    font-size: 16px;
	}

	.cart-item__data button i.fas {
	    background: red;
	    color: #FFF;
	    padding: 3px;
	    border-radius: 2px;
	}
	#storefront-app h1 {
	    text-transform: uppercase;
	}

	.cart__info, .cart__info p, .cart__info a, .cart__info span {
	    color: #fff;
	}
	.cart__summary-row.cart__total .prices__value {
	    color: #FFF;
	    font-size: 29px;
	}
	footer.card-footer a:hover {
	    background: #03a503;
	    border-color: #03a503;
	}
