/* Geral */
#header,.top-bar,.footer {
    background: #000 !important;
    background-color: #000 !important;
    color: #fff !important;
}
#header a,.top-bar a,.footer  a {
    color: #fff !important;
}
.menu__social .fab,
.footer__social .fab{
    color: #FFF !important;
}

/* Menu lateral */
#menu .menu__item {
    color: #FFF;
}


/* Footer */
.footer__stamps li {
    margin: 2px 0;
    padding: 2px 0px;
}
.footer__stamps img {
    width: auto !important;
    max-height: 43px !important;
    background: #FFF;
}

/* Blog */
.blog .card .card-body {
    text-align: center;
    background: #FFF;
}

/* Produtos */
.product-card {
    background: #000000;
    color: #FFF;
    text-align: center;
}
.product-card .product-card__name {
    margin: 8px 0 !important;
    padding: 0 0 0 0 !important;
    height: auto;
    font-size: 14px;
    color: #FFF;
}
.product-card__buy button.btn {
    color: #FFF;
    background-color: #424242 !important;
    opacity: 1 !important;
    display: block;
    width: auto;
    margin: 13px auto 0 auto;
}
